package InterviewProgramms;

public class EvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=2;
		for (int i=0; i<=num; i++)
		{
			System.out.println(i);
		if(i%2==0)
		{
		System.out.println("even");	
		}
		else
		{
			System.out.println("ODD");
		}
		}
		
	}

}
